/**
 * Ejercicio 3. El cifrado indescifrable
 * @module Cifrado
 * @author Andrea Hernández Martín
 */

/**
 * @type {Alfabeto} El alfabeto del cifrado a usar
 */
export type Alfabeto = 'ABCDEFGHIJKLMNÑOPQRSTUVWXYZ';

/**
 * Clase abstracta Cifrado
 */
export abstract class Cifrado {
  /**
   * Constructor de la clase abstracta Cifrado
   * @param alfabeto Alfabeto a utilizar para cifrar y descifrar
   * @param mensaje Mensaje a cifrar y descifrar
   * @param clave Clave con la que queremos cifrar y descifrar
   */
  constructor(public readonly alfabeto: Alfabeto, public mensaje: string, public clave: string) {
  }

  /**
   * Función que elimina los espacios del mensaje y la clave, y además pone sus letras a mayúsculas
   */
  eliminarEspacios(): void {
    this.mensaje = this.mensaje.replace(/ /g, '').toUpperCase();
    this.clave = this.clave.replace(/ /g, '').toUpperCase();
  }

  /**
   * Función que repite la clave en caso de que sea el mensaje a cifrar mayor o la recorta en caso de que sea menor
   * @returns Retorna la clave con el mismo tamaño que el mensaje a cifrar
   */
  repetirClave(): string {
    let auxClave: string = '';
    let contador: number = 0;
    let aux: string = '';

    // Clave más pequeña que el mensaje
    if (this.clave.length < this.mensaje.length) {
      let i: number = 0;
      while (i < this.mensaje.length) {
        if (contador >= this.clave.length) {
          contador = 0;
        } else {
          aux = this.clave.charAt(contador);
          auxClave += aux;

          contador++;
          i++;
        }
      }
    } else { // Clave mayor que el mensaje
      for (let i = 0; i < this.mensaje.length; i++) {
        auxClave += this.clave.charAt(i);
      }
    }
    return auxClave;
  }

  abstract print(): void;
}

/**
 * Ejercicio 3. El cifrado indescifrable
 * @module Decodifación
 * @author Andrea Hernández Martín
 */

import {Cifrado} from "./cifrado";
import {Alfabeto} from "./cifrado";

export class Decodificacion extends Cifrado {
  constructor(alfabeto: Alfabeto, mensaje: string, clave: string) {
    super(alfabeto, mensaje, clave);
  }

  decodificacion(): string {
    let msgCifrado: string = '';
    this.eliminarEspacios();
    const auxClave = this.repetirClave();
    const suma: number[] = [];

    for (let i = 0; i < this.mensaje.length; i++) {
      if (this.alfabeto.search(this.mensaje[i]) >= this.alfabeto.search(auxClave[i])) {
        suma.push((this.alfabeto.search(this.mensaje[i]) - this.alfabeto.search(auxClave[i]) - 1) % 27);
      } else {
        suma.push((this.alfabeto.search(this.mensaje[i]) - this.alfabeto.search(auxClave[i]) - 1) + 27);
      }
    }

    for (let i = 0; i < suma.length; i++) {
      msgCifrado += this.alfabeto.charAt(suma[i]);
    }
    this.mensaje = msgCifrado;
    return this.mensaje;
  }

  print(): void {
    console.log(`El mensaje ${this.mensaje} cifrado es: ${this.decodificacion()}`);
  }
}

const deco1 = new Decodificacion('ABCDEFGHIJKLMNÑOPQRSTUVWXYZ', 'kamw', 'clave');
deco1.print();
const deco2 = new Decodificacion('ABCDEFGHIJKLMNÑOPQRSTUVWXYZ', 'kamwqxyel', 'clave');
deco2.print();

/**
 * Modificación
 * @module Modificación
 * @author Andrea Hernández Martín
 */


/**
 * Interfaz Collectable de tipo generico T
 */
interface Collectable<T> {
  addItem(newItem: T): void;
  getItem(indice: number): T;
  removeItem(indice: number): void;
  getNumberOfItems(): number;
}

/**
 * Interfaz genérica Printable
 */
interface Printable {
  print(): void;
}

/**
 * Clase abstracta PrintableCollection de tipo genérico
 */
abstract class PrintableCollection<T> implements Collectable<T>, Printable {
  /**
   * Constructor de la clase abstracta PrintableCollection
   * @param items array de valores de la clase
   */
  constructor(protected items: T[]) {
  }

  /**
   * Función que añade un item nuevo
   * @param newItem item que se añade al array de items
   */
  addItem(newItem: T): void {
    this.items.push(newItem);
  }

  /**
   * Función que devuelve el item según el índice
   * @param indice Lugar donde se encuentra el item a buscar
   * @returns Devuleve el item que se encuentra en el lugar del índice que se especifica
   */
  getItem(indice: number): T {
    return this.items[indice];
  }

  /**
   * Función que elimina un item de la posision que se le pasa por índice
   * @param indice Posision del item a eliminar
   */
  removeItem(indice: number): void {
    this.items.splice(indice, 1);
  }

  /**
   * Función que devuelve el numero de items total que hay
   * @returns El numero de items en el array
   */
  getNumberOfItems(): number {
    return this.items.length;
  }

  /**
   * Función print abstracta
   */
  abstract print(): void;
}

/**
 * Clase NumericPrintableCollection que hereda de la clase PrintableCollection
 */
export class NumericPrintableCollection extends PrintableCollection<number> {
  /**
   * Constructror de la clase
   * @param items array de items
   */
  constructor(items: number[]) {
    super(items);
  }

  /**
   * Funcion que imprime la colección de valores numericos
   */
  print(): void {
    const it: number[] = this.items;
    const arrStr: string[] = [];
    // let result: string = '';
    it.forEach((element) => {
      arrStr.push((element.toString()));
    });
    console.log(arrStr.join(','));
  }
}

/**
 * Clase StringPrintableCollection que hereda de la clase PrintableCollection
 */
export class StringPrintableCollection extends PrintableCollection<string> {
  /**
   * Constructror de la clase
   * @param items array de items
   */
  constructor(items: string[]) {
    super(items);
  }

  /**
   * Funcion que imprime la colección de valores de string
   */
  print(): void {
    const arrStr: string[] = this.items;
    console.log(arrStr.join(','));
  }
}

const num = new NumericPrintableCollection([1, 2]);
num.print();
const str = new StringPrintableCollection(["hola", "mundo"]);
str.print();
